const getPlayers = name => {
  return fetch(`https://www.balldontlie.io/api/v1/players?search=${name}`);
};
const getAverages = year => {
  return fetch(
    `https://www.balldontlie.io/api/v1/season_averages?season=${year}&player_ids[]=1`
  );
};

export { getPlayers, getAverages };
