import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";

export default function Players() {
  const [players, setPlayers] = useState({
    name: "lebron",
    year: "2017",
    ids: "237",
    stats: {}
  });

  useEffect(() => {
    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data.data);
      })
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    getAverages(players.year, players.ids)
      .then(res => {
        return res.json();
      })
      .then(data => {
        // setPlayers(...players, stats: data.data[0])
        console.log(data.data[0]);
      })
      .catch(error => console.error(error));
  }, []);

  return (
    <form action="">
      {/* <input 
    type="text"
    value = {players.name}
    onChange={e => setPlayers(...players, name: e.target.name })}
    /> */}
    </form>
  );
}
