import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";
import axios from "axios";
import "./Players.scss";

export default function Players() {
  const [players, setPlayers] = useState({
    firstName: "lebron",
    lastName: "",
    year: "2017",
    stats: {}
  });

  const handleOnSubmit = e => {
    e.preventDefault();
    console.log(players.firstName);
    console.log(players.lastName);
    console.log(players.stats);
  };

  const fetchData = () => {
    const playersAPI = `https://nba-players.herokuapp.com/players-stats/${players.firstName}/${players.lastName}`;
    const statsAPI =
      "https://www.balldontlie.io/api/v1/stats?seasons[]=2018&player_ids[]=1043";

    const getPlayer = axios.get(playersAPI);
    const getStats = axios.get(statsAPI);

    axios.all([getPlayer, getStats]).then(
      axios.spread((...allData) => {
        const allDataPLayers = allData[0];

        console.log(allDataPLayers.data);

        setPlayers({ ...players, stats: allDataPLayers.data });
      })
    );
  };
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="players">
      <form action="" className="players__form" onSubmit={handleOnSubmit}>
        <input
          type="text"
          value={players.firstName}
          onChange={e => setPlayers({ ...players, firstName: e.target.value })}
        />
        <button>search</button>
      </form>

      <div className="card">
        <img src="" alt="" />
        <div className="card_stats"></div>
      </div>
    </div>
  );
}
