const getPlayers = name => {
  return fetch(`https://www.balldontlie.io/api/v1/lebron`);
};
const getAverages = (year, ids) => {
  return fetch(
    `https://www.balldontlie.io/api/v1/season_averages?season=${year}&player_ids[]=${ids}`
  );
};
const getTeams = () => {
  return fetch("https://www.balldontlie.io/api/v1/teams");
};

const getPlayersOnTeam = name => {
  return fetch(`https://nba-players.herokuapp.com/players-stats-teams/${name}`);
};

export { getPlayers, getAverages, getTeams, getPlayersOnTeam };
