import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";
import axios from "axios";
import "./Players.scss";

export default function Players() {
  const [players, setPlayers] = useState({
    name: "",
    stats: {}
  });

  const handleOnSubmit = e => {
    e.preventDefault();
    console.log(players.name);
    console.log(players.stats);
  };

  const fetchData = () => {
    const playersAPI = `https://www.balldontlie.io/api/v1/players?search=${players.name}`;

    const getPlayer = axios.get(playersAPI);

    axios.all([getPlayer]).then(
      axios.spread((...allData) => {
        const allDataPLayers = allData[0];

        console.log(allDataPLayers.data);

        setPlayers({ ...players, stats: allDataPLayers.data });
      })
    );
  };
  useEffect(() => {
    fetchData();
  }, []);
  // useEffect(() => {
  //   getPlayers(players.name)
  //     .then(res => {
  //       return res.json();
  //     })
  //     .then(data => {
  //       console.log(data);

  //       // setPlayers({ ...players, stats: data.data[0] });
  //     })
  //     .catch(console.error);
  // }, []);

  return (
    <div className="players">
      <form className="players__form" onSubmit={handleOnSubmit}>
        <input
          type="text"
          value={players.name}
          onChange={e => setPlayers({ ...players, name: e.target.value })}
        />
        <button>search</button>
      </form>

      <div className="card">
        <img src="" alt="" />
        <div className="card_stats"></div>
      </div>
    </div>
  );
}
