import React, { useState, useEffect } from "react";
// import { getPlayers, getAverages } from "../../api/api";
import axios from "axios";

export default function Players() {
  const [players, setPlayers] = useState({
    firstName: "Adams",
    lastName: "Steven",
    year: "2017",
    stats: {},
    ids: " "
  });

  const fetchData = () => {
    const playersAPI = `https://nba-players.herokuapp.com/players-stats/${players.firstName}/${players.lastName}`;
    const statsAPI =
      "https://www.balldontlie.io/api/v1/stats?seasons[]=2018&player_ids[]=1043";

    const getPlayer = axios.get(playersAPI);
    const getStats = axios.get(statsAPI);

    axios.all([getPlayer, getStats]).then(
      axios.spread((...allData) => {
        const allDataPLayers = allData[0];
        const allStatsPlayers = allData[1];

        // setPlayers({ ...players, ids: allDataPLayers.data.data[0].id });

        console.log(allDataPLayers);
        console.log(allStatsPlayers);
      })
    );
  };
  console.log(players.ids);

  useEffect(() => {
    fetchData();
  }, []);

  // const playerApi = () => {
  //   return fetch(`https://www.balldontlie.io/api/v1/players?search=kobe`);
  // };
  // const statsApi = () => {
  //   return fetch(
  //     `https://www.balldontlie.io/api/v1/season_averages?season=2017&player_ids[]=1043`
  //   );
  // };

  // useEffect(() => {
  //   Promise.all([playerApi, statsApi]).then(results => {
  //     console.log(results.json());
  //   });
  // }, []);

  return (
    <div>
      <form action="">
        <label>
          First Name
          <input
            type="text"
            value={players.firstName}
            onChange={e => setPlayers({ ...players, firstName: e.target.name })}
          />
        </label>

        <label>
          Last Name
          <input
            type="text"
            value={players.lastName}
            onChange={e => setPlayers({ ...players, lastName: e.target.name })}
          />
        </label>

        <button>search</button>
      </form>
    </div>
  );
}
