const getPlayers = (firstName, lastName) => {
  return fetch(
    `https://nba-players.herokuapp.com/players-stats/${firstName}/${lastName}`
  );
};
const getAverages = (year, ids) => {
  return fetch(
    `https://www.balldontlie.io/api/v1/season_averages?season=${year}&player_ids[]=${ids}`
  );
};
const getTeams = () => {
  return fetch("https://www.balldontlie.io/api/v1/teams");
};

export { getPlayers, getAverages, getTeams };
