const getPlayers = name => {
  return fetch(`https://www.balldontlie.io/api/v1/players?search=${name}`);
};
const getAverages = (year, ids) => {
  return fetch(
    `https://www.balldontlie.io/api/v1/season_averages?season=${year}&player_ids[]=${ids}`
  );
};
const getTeams = () => {
  return fetch("https://nba-players.herokuapp.com/");
};

export { getPlayers, getAverages, getTeams };
