import React, { useState, useEffect } from "react";
import { getPlayers } from "../../api/api";
import "./Players.scss";
import NewPlayer from "./NewPlayer";

export default function Players() {
  const [players, setPlayers] = useState({
    name: null,
    stats: {}
  });
  const [newPlayer, setNewPlayer] = useState();

  const handleOnSubmit = event => {
    event.preventDefault();
    setNewPlayer(players.stats);
  };

  const handleChange = e => {
    console.log(e.target.value);

    const replace = e.target.value;
    if (replace.length > 3) {
      setPlayers({ ...players, name: replace });
    }
  };
  console.log(newPlayer);

  useEffect(() => {
    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(data => {
        if (data.data[0] === undefined) {
          return null;
        } else {
          setPlayers({ ...players, stats: data.data[0] });
        }
      })
      .catch(err => console.log(err));
  }, [players.name]);

  return (
    <div className="players">
      <form action="" className="players__form" onSubmit={handleOnSubmit}>
        First Name
        <input type="text" value={players.name} onChange={handleChange} />
        <button type="submit">search</button>
      </form>

      <NewPlayer newPlayer={newPlayer} />
    </div>
  );
}
