import React, { useEffect, useState } from "react";
import { getTeams } from "../../api/api";
import "./Teams.scss";

export default function Teams() {
  const [teams, setTeams] = useState([]);

  //   const fetchData = () => {
  //     const teamAPI = "https://www.balldontlie.io/api/v1/teams";

  //     const getTeams = axios.get(teamAPI);

  //     axios.all([getTeams]).then(
  //       axios.spread((...allData) => {
  //         const allDataTeams = allData[0];
  //         setTeams({ ...teams, team: allDataTeams.data.data });
  //       })
  //     );
  //   };
  //   console.log(teams.team);

  //   useEffect(() => {
  //     fetchData();
  //   }, []);

  useEffect(() => {
    getTeams()
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data.data);

        setTeams(data.data);
      })
      .catch(error => console.error(error));
  }, []);

  return (
    <div className="wrapper">
      <ul>
        {teams.map((team, i) => {
          return <p key={i}>{team.name}</p>;
        })}
      </ul>
    </div>
  );
}
