import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";
import axios from "axios";

export default function Players() {
  const [players, setPlayers] = useState({
    name: "kobe",
    year: "2017",
    stats: {},
    ids: ""
  });

  const fetchData = () => {
    const playersAPI = `https://www.balldontlie.io/api/v1/players?search=${players.name}`;
    const statsAPI = `https://www.balldontlie.io/api/v1/season_averages?season=${players.year}&player_ids[]=${players.ids}`;

    const getPlayers = axios.get(playersAPI);
    const getStats = axios.get(statsAPI);

    axios.all([getPlayers, getStats]).then(
      axios.spread((...allData) => {
        const allDataPLayers = allData[0];
        const allStatsPlayers = allData[1];

        setPlayers({ ...players, ids: allDataPLayers.data.data[0].id });

        console.log(allDataPLayers);
        console.log(allStatsPlayers.data.data[0]);
      })
    );
  };
  // console.log(players.ids);

