import React, { useState, useEffect } from "react";
import { getPlayers } from "../../api/api";
import "./Players.scss";
import NewPlayer from "./NewPlayer";

export default function Players() {
  const [players, setPlayers] = useState({
    name: " ",
    stats: {}
  });

  const handleChange = e => {
    const replace = e.target.value;

    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(({ data }) => {
        if (data.length) {
          setPlayers({ ...players, stats: data, name: replace });
        }
      })
      .catch(err => console.log(err));
  };
  console.log(players.stats);
  console.log(players.name);

  return (
    <div className="players">
      <form className="players__form">
        First Name
        <input type="text" value={players.value} onChange={handleChange} />
      </form>

      {players.stats.length ? (
        <NewPlayer list={players.stats} name={players.name} />
      ) : null}
    </div>
  );
}
