import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";
import axios from "axios";
import "./Players.scss";

export default function Players() {
  const [players, setPlayers] = useState({
    firstName: "james",
    lastName: "lebrone",
    year: "2017",
    stats: {}
  });

  const handleOnSubmit = e => {
    e.preventDefault();
    console.log(players.firstName);
    console.log(players.lastName);
  };

  // const fetchData = () => {
  //   const playersAPI = `https://nba-players.herokuapp.com/players-stats/${players.firstName}/${players.lastName}`;
  //   const statsAPI =
  //     "https://www.balldontlie.io/api/v1/stats?seasons[]=2018&player_ids[]=1043";

  //   const getPlayer = axios.get(playersAPI);
  //   const getStats = axios.get(statsAPI);

  //   axios.all([getPlayer, getStats]).then(
  //     axios.spread((...allData) => {
  //       const allDataPLayers = allData[0];
  //       const allStatsPlayers = allData[1];

  //       setPlayers({ ...players, stats: allDataPLayers.data });
  //     })
  //   );
  // };
  // useEffect(() => {
  //   fetchData();
  // }, []);

  useEffect(() => {
    getPlayers(players.firstName, players.lastName)
      .then(res => {
        return res.text();
      })
      .then(text => console.log(text))
      // .then(data => {
      //   console.log(data);
      // })
      .catch(error => console.error(error));
  }, []);

  return (
    <div className="players">
      <form action="" className="players__form" onSubmit={handleOnSubmit}>
        <label>
          First Name
          <input
            type="text"
            value={players.firstName}
            onChange={e =>
              setPlayers({ ...players, firstName: e.target.value })
            }
          />
        </label>

        <label>
          Last Name
          <input
            type="text"
            value={players.lastName}
            onChange={e => setPlayers({ ...players, lastName: e.target.value })}
          />
        </label>
        <button>search</button>
      </form>

      <div className="card">
        <img src="" alt="" />
        <div className="card_stats"></div>
      </div>
    </div>
  );
}
