import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";

export default function Players() {
  const [players, setPlayers] = useState({
    name: "kobe",
    year: "2017",
    stats: {},
    ids: " "
  });

  useEffect(() => {
    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(data => {
        setPlayers({ ...players, ids: data.data[0].id });
        console.log(data.data[0]);
      })
      .then(() => getAverages(players.year))
      .then(res => {
        return res.json();
      })
      .then(data => {
        setPlayers({ ...players, stats: data.data[0] });
        console.log(data.data[0]);
      })
      .catch(error => console.error(error));
  }, []);
  console.log(players.ids);

  // Promise.all([p, p1]).then(([res1, res2]) => {
