import React, { useState, useEffect } from "react";
import { getPlayers } from "../../api/api";
import "./Players.scss";

export default function Players() {
  const [players, setPlayers] = useState({
    name: null,
    stats: {}
  });

  const handleOnSubmit = event => {
    event.preventDefault();
  };

  const handleChange = e => {
    console.log(e.target.value);

    const replace = e.target.value;
    if (replace.length > 3) {
      setPlayers({ ...players, name: replace });
    }
  };

  useEffect(() => {
    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(async data => {
        console.log(data.data[0]);
        if (data.data[0] === undefined) {
          alert("Enter the correct name");
        } else {
          await setPlayers({ ...players, stats: data.data[0] });
        }
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="players">
      <form action="" className="players__form" onSubmit={handleOnSubmit}>
        <input type="text" value={players.name} onChange={handleChange} />
        <button type="submit">search</button>
      </form>

      <div className="card">
        <img src="" alt="" />
        <div className="card_stats"></div>
      </div>
    </div>
  );
}
