import React, { useRef, useEffect } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useLocation
} from "react-router-dom";
import "./App.scss";

import logo from "./logo.png";
import players from "./players.png";
import Players from "./pages/Players/Players";
import Teams from "./pages/Teams/Teams";
import Games from "./pages/Games/Games";

function App() {
  const ref = useRef();

  const handleClick = () => {
    const { pathname } = useLocation();

    useEffect(() => {
      window.scrollTo(ref);
    }, [pathname]);

    return null;
  };

  return (
    <div className="App">
      <header className="header">
        <Router>
          <span></span>

          <div className="wrapper">
            <img src={logo} alt="" />

            <ul className="header__list">
              <li className="header__list_item margin-left">
                <a onClick={handleClick}>Players</a>
              </li>
              {/* <li className="header__list_item margin-left">
                <Link to="/players">Players</Link>
              </li> */}

              <li className="header__list_item">
                <Link to="/teams">Teams</Link>
              </li>

              <li className="header__list_item">
                <Link to="/games">Games</Link>
              </li>
            </ul>
          </div>

          <div className="tagline">
            <p>Basketball is life</p>
            <p className="tagline_margin">The rest is just details</p>
          </div>

          <Switch>
            <Route exact path="/players" component={Players} />
            <Route exact path="/teams" component={Teams} />
            <Route exact path="/games" component={Games} />
          </Switch>
        </Router>
      </header>

      <main>
        <article className="main__wrapper">
          <img src={players} alt="player" className="players-img" />

          <div className="main__wrapper-players">
            <div className="text-alight">
              <h3>All about NBA</h3>
              <a href="">Players</a>
              <span>You can find out about the player and his statistics.</span>
            </div>
          </div>

          <div className="main__wrapper-teams">
            <div className="text-alight">
              <a href="">Teams</a>
              <span>Find out all the teams and their lineups.</span>
            </div>
          </div>

          <div className="main__wrapper-games">
            <div className="text-alight">
              <a href="">Games</a>
              <span>
                Find all the games in recent years and their statistics.
              </span>
            </div>
          </div>
        </article>
      </main>
    </div>
  );
}

export default App;
