import React, { useState, useEffect } from "react";
import { getPlayers, getAverages } from "../../api/api";

export default function Players() {
  const [players, setPlayers] = useState({
    name: "lebron",
    year: "2017",
    stats: {},
    ids: ""
  });
  console.log(players.stats);

  // const p = getPlayers(players.name);
  // const p1 = getAverages(players.year, players.ids);

  // Promise.all([p, p1]).then(([res1, res2]) => {
  //   setPlayers({ ...players, ids: res1.data[0].id });
  //   setPlayers({ ...players, stats: res2.data[0] });
  // });

  useEffect(() => {
    getPlayers(players.name)
      .then(res => {
        return res.json();
      })
      .then(data => {
        setPlayers({ ...players, ids: data.data[0].id });
        console.log(data.data[0]);
      })
      .then(() => getAverages(players.year, players.ids))
      .then(res => {
        return res.json();
      })
      .then(data => {
        setPlayers({ ...players, stats: data.data[0] });
        console.log(data.data[0]);
      })
      .catch(error => console.error(error));
  }, []);

  // useEffect(() => {
