import React, { useState, useEffect } from "react";
import { Card, Icon, Image } from "semantic-ui-react";
import { getPlayerImg } from "../../api/api";

export default function NewPlayer({ newPlayer }) {
  const [img, setImg] = useState("");
  useEffect(() => {
    getPlayerImg(newPlayer.last_name, newPlayer.first_name)
      .then(res => {
        return setImg(res.url);
      })
      //   .then(data => {
      //     console.log(data);
      //     setImg(data);
      //   })
      .catch(err => console.log(err));
  }, []);

  return (
    <div>
      <img src={img} alt="" />
      <h3>{newPlayer.last_name + " " + newPlayer.first_name}</h3>
      <p></p>
    </div>
  );
}
